if (msg.payload) {
    global.set("bitratechange", false);
    let sr = parseInt(global.get("device.SR")) / 1000;
    global.set("settings.SR", sr);
    let audioBitRate = parseInt(global.get("settings.ABITRATE"));
    let videoBitRate = 0;

    let fec = global.get("device.FEC");
    let numerator = parseInt(fec.substr(0, 1));
    let denominator = parseInt(fec.substr(2, 2));
    let fecCalculated = numerator / denominator;
    let constelMode =
        global.get("device.Constel") === "qpsk" ? 2 :
            global.get("device.Constel") === "8psk" ? 3 :
                global.get("device.Constel") === "16apsk" ? 4 :
                    5;
    let qpskTsBitRate = constelMode * sr * 188 / 204 * fecCalculated * 1075 / 1000;
    global.set("settings.TSBITRATE", qpskTsBitRate.toFixed(2));
    if (sr > 20 && sr < 36) {
        videoBitRate = (1000 * qpskTsBitRate * 50 / 100 / 1000) - audioBitRate;
    }
    if (sr > 35 && sr < 67) {
        videoBitRate = (1000 * qpskTsBitRate * 60 / 100 / 1000) - audioBitRate;
    }
    if (sr > 66 && sr < 126) {
        videoBitRate = (1000 * qpskTsBitRate * 68 / 100 / 1000) - audioBitRate;
    }
    if (sr > 125 && sr < 251) {
        videoBitRate = (1000 * qpskTsBitRate * 76 / 100 / 1000) - audioBitRate;
    }
    if (sr > 250 && sr < 334) {
        videoBitRate = (1000 * qpskTsBitRate * 80 / 100 / 1000) - audioBitRate;
    }
    if (sr > 333 && sr < 501) {
        videoBitRate = (1000 * qpskTsBitRate * 82 / 100 / 1000) - audioBitRate;
    }
    if (sr > 500 && sr < 1001) {
        videoBitRate = (1000 * qpskTsBitRate * 86 / 100 / 1000) - audioBitRate;
    }
    if (sr > 1000 && sr < 1501) {
        videoBitRate = (1000 * qpskTsBitRate * 87 / 100 / 1000) - audioBitRate;
    }
    if (sr > 1500 && sr < 3001) {
        videoBitRate = (1000 * qpskTsBitRate * 87 / 100 / 1000) - audioBitRate;
    }
    msg.payload = qpskTsBitRate.toFixed(2);
    global.set("settings.VBITRATE", videoBitRate.toFixed(2));
    msg.vidbr = videoBitRate?.toFixed(2);
    return msg;
