if (ffmpegPID === "" || ffmpegPID === undefined) {
    /* 
        In here be dragons...
     */
    let videoBitrate = global.get("settings.VBITRATE");
    let tsRate = global.get("settings.TSBITRATE");
    let codec = global.get("settings.CODEC")
    let hWEncoderSettings = `-r ${global.get("settings.FPS")} -rc-lookahead 10 -no-scenecut 1 -zerolatency 1 -b_ref_mode 0 -bf 0`;
    let shapingString = `-minrate ${global.get("settings.VBITRATE")}K -maxrate ${global.get("settings.VBITRATE")}K`;
    switch (codec) {
        case 'libx264':
            codec = `libx264 -r ${global.get("settings.FPS")} ${shapingString}`;
            break;
        case 'libx265':
            // codec = `libx265 -x265-params b-adapt=1 `;
            codec = `libx265 -r ${global.get("settings.FPS")} ${shapingString}`;
            break;
        case 'libvvenc':
            let vvcBitRate = Math.round(videoBitrate * 800 ); // to Mega-ish
            codec = `libvvenc -preset faster -r ${global.get("settings.FPS")} -vvenc-params bitrate=${vvcBitRate}:framerate=${global.get("settings.FPS")}:size=${global.get("settings.IMAGESIZE")}`;
            break;
        case 'libaom-av1':
            codec = `libaom-av1 -crf 30 -usage realtime ${shapingString}`;
            break;
        case 'h264_nvenc':
            codec = `h264_nvenc ${hWEncoderSettings} ${shapingString}`;
            break;
        case 'hevc_nvenc':
            codec = `hevc_nvenc ${hWEncoderSettings} ${shapingString}`;
            break;     
    }
    let offset = global.get("settings.audio_delay");
    /* These need to be stored in json and get GUI inputs */
    let thread_queue = global.get("settings.thread_queue");
    let rt_buffer = global.get("settings.rt_buffer");
    let symbol_rate = global.get("settings.SR");
    let buffer_factor = symbol_rate < 66 ? 3 : 1;
    let buffer_size = videoBitrate * buffer_factor;
    let video_pid = 256;
    let mpegts_start_pid = 256;
    let audio_pid = 257;
    let keyframe_interval = global.get("settings.keyframe_interval");
    let max_delay = symbol_rate < 36 ? 2000: 1000;
    let max_interleave_delta = symbol_rate < 36 ? 0 : 4;
    let pcr_period = 20;
    let pat_period = 0.4;
    let mpegts_service_id = 4095;
    let mpegts_transport_stream_id = 4095;
    let mpegts_pmt_start_pid = 4095;
    let mpegts_original_network_id = 1;
    let service_provider = "DATV-RED";
    let fifo_size = global.get("settings.fifo_size");
    /* 
        This is the big ffmpeg variable string
        do note the space ' ' at the end of each line...
    */
    let str =
        `-itsoffset ${offset} ` +
        `-f dshow -thread_queue_size ${thread_queue}K -rtbufsize ${rt_buffer}M ` +
        `-i \"video=${global.get("settings.video_device")}\" ` +
        `-f dshow -thread_queue_size ${thread_queue}K -rtbufsize ${rt_buffer}M ` +
        `-i \"audio=${global.get("settings.audio_device")}\" ` +
        `-ar ${global.get("settings.ABITRATE")}K ` +
        `-vcodec ${codec} ` +
        `-s ${global.get("settings.IMAGESIZE")} ` +
        `-b:v ${global.get("settings.VBITRATE")}K ` +        
        `-bufsize ${buffer_size}K -g ${keyframe_interval} ` +
        `-acodec libfdk_aac ` +
        `-ac ${global.get("settings.AUDIO")} ` +
        `-b:a ${global.get("settings.ABITRATE")}k ` +
        `-f mpegts -muxrate ${tsRate}K ` +
        `-streamid 0:${video_pid} ` +
        `-streamid 1:${audio_pid} ` +
        `-max_delay ${max_delay}K ` +
        `-max_interleave_delta ${max_interleave_delta}M ` +
        `-pcr_period ${pcr_period} ` +
        `-pat_period ${pat_period} ` +
        // `-mpegts_service_id ${mpegts_service_id} ` +
        `-mpegts_original_network_id ${mpegts_original_network_id} ` +
        `-mpegts_transport_stream_id ${mpegts_transport_stream_id} ` +
        `-mpegts_pmt_start_pid ${mpegts_pmt_start_pid} ` +
        `-mpegts_start_pid ${mpegts_start_pid} ` +
        `-metadata service_provider=${service_provider} ` +
        `-metadata service_name=${global.get("device.callsign")} ` +
        `-af aresample=async=1 ` +
        `\"udp://${global.get("device.tx.dvbs2.tssourceaddress")}?pkt_size=1316&overrun_nonfatal=1&fifo_size=${fifo_size}M\"`;
        // Loopback for local testing without txing...
        // `\"udp://${global.get("device.rx.tsip")}?pkt_size=1316&overrun_nonfatal=1&fifo_size=${fifo_size}M\"`;


    msg.payload = str;
    global.set("ffmpeg_cmd", msg.payload);
    return msg;
}

