switch (constel) {
    case "qpsk":
        if (frame === "long") msg.options = ["1/4", "1/3", "2/5", "1/2", "3/5", "2/3", "3/4", "4/5", "5/6", "8/9", "9/10"];
        if (frame === "short") msg.options = ["1/4", "1/3", "2/5", "1/2", "3/5", "2/3", "3/4", "4/5", "5/6", "8/9"];
        return msg;
    case "8psk":
        if (frame === "long") msg.options = ["3/5", "2/3", "3/4", "5/6", "8/9", "9/10"];
        if (frame === "short") msg.options = ["3/5", "2/3", "3/4", "5/6", "8/9"];
        return msg;
    case "16apsk":
        if (frame === "long") msg.options = ["2/3", "3/4", "5/6", "8/9", "9/10"];
        if (frame === "short") msg.options = ["2/3", "3/4", "5/6", "8/9"];
        return msg;
    case "32apsk":
        if (frame === "long") msg.options = ["3/4", "5/6", "8/9", "9/10"];
        if (frame === "short") msg.options = ["3/4", "5/6", "8/9"];
        return msg;
}

